    "Title": "192行",
    "Content": "//wangjianshuo@baixing.com<br/>namespace Graph;<br/><br/>class CompanyAccount {<br/>...function occuredRevenue($startTime, $endTime) {<br/>......$money = 0;<br/>......<br/>......if($endTime &gt; time())<br/>.........throw new Exception(\"Refuse to tell you future, because that may be inaccurate\");<br/>......<br/>......$s = new Searcher(<br/>.........new AndQuery(<br/>............new Query('type', 'Order'),<br/>............new Query('paid', 1),<br/>............new RangeQuery('startTime', null, $endTime),<br/>............new RangeQuery('endTime', $startTime - 1, null)<br/>.........)<br/>......);<br/>......<br/>......foreach($s as $order)<br/>.........$money += ($order-&gt;occuredRevenue($endTime) - $order-&gt;occuredRevenue($startTime));<br/>.........<br/>......return $money;<br/>...}<br/>}<br/><br/>class UserAccount {<br/>...public $balance; // money + credit;<br/>...public $ratio; // money / balance;<br/>...<br/>...function in($money, $credit){<br/>......$this-&gt;ratio = ($this-&gt;balance * $this-&gt;ratio + $money) / ($this-&gt;balance + $money + $credit);<br/>......$this-&gt;balance += $money + $credit;<br/>......$this-&gt;save();<br/>...}<br/>...<br/>...function out($mondit) { //mondit = money + credit<br/>......$this-&gt;balance -= $mondit;<br/>......$this-&gt;save();<br/>...}<br/>...<br/>...function pay($order) {<br/>......$order-&gt;pay($order-&gt;listPrice * $this-&gt;ratio);<br/>......$this-&gt;out($order-&gt;listPrice);<br/>...}<br/>...<br/>...function partialCancel($order, $time) {<br/>......$order-&gt;particalCancel($time);<br/>......<br/>......$refund = $order-&gt;price - $order-&gt;occuredRevenue($time);<br/>......$ratio = $order-&gt;price / $order-&gt;listPrice;<br/><br/>......$this-&gt;in($refund, $refund / $ratio * (1 - $ratio));<br/>...}<br/>}<br/><br/>class Order {<br/>...public $unitTime = 24 * 60 * 60; // 1 day<br/>...public $paid = false;<br/>...<br/>...function occuredRevenue($time) {<br/>......$money = 0;<br/>......$unitPrice = round(($this-&gt;price) / ($this-&gt;endTime - $this-&gt;startTime) * $this-&gt;unitTime, 2);<br/>......for($i = 0; $occuredTime = $this-&gt;startTime + $this-&gt;unitTime * $i, $occuredTime &lt; min($time, $endTime); $i++)<br/>.........if($this-&gt;endTime - $this-&gt;occuredTime &lt;= $this-&gt;unitTime)<br/>............$money += ($this-&gt;price - $unitPrice * $i);<br/>.........else<br/>............$money += $unitPrice;<br/>......return $money;......<br/>...}<br/>...<br/>...function place() {<br/>......$pa = new BiddingPrice();<br/>......$this-&gt;listPrice = $pa-&gt;price($order);<br/>......$this-&gt;save();<br/>...}<br/>...<br/>...function partialCancel($time) {<br/>......if($time &gt;= $this-&gt;endTime)<br/>.........throw new Exception('Cannot cancel completed order');<br/>......<br/>......$neg = clone $this;<br/>......$neg-&gt;startTime = max($this-&gt;startTime, $time);<br/>......$neg-&gt;price = $this-&gt;occuredRevenue($time) - $this-&gt;price;<br/>......$neg-&gt;save();<br/>...}<br/>...<br/>...function attributes() {<br/>......$ad = graph($this-&gt;adId);<br/>......<br/>......return array_merge(<br/>.........parent::attributes(),<br/>.........'categories' =&gt; cg_array_upto(array_reverse($ad-&gt;category-&gt;path()), $this-&gt;category),<br/>.........'areas' =&gt; cg_array_upto(array_reverse($ad-&gt;area-&gt;path()), $this-&gt;area)<br/>...}<br/>}<br/><br/>function cg_array_upto($arr, $c) {<br/>...$result = array();<br/>...<br/>...foreach($arr as $a) {<br/>......$result[] = $a;<br/>......$if($a == $c)<br/>.........break;<br/>...}<br/>...return $result;<br/>}<br/><br/>class DingPrice extends BiddingPrice {<br/>...public $capacity = 12;<br/>...public $sensitivity = 0.5;<br/>...public $bottom = 3;<br/>...public $tolerance = 30; // day<br/>...public $basePrice = 10; // RMB<br/>}<br/><br/>class RefreshPrice extends BiddingPrice {<br/>...public $capacity = 12;<br/>...public $sensitivity = 0.5;<br/>...public $bottom = 3;<br/>...public $tolerance = 30; // day<br/>...public $basePrice = 10; // RMB<br/>}<br/><br/>class BiddingPrice {<br/>...function price($order) {<br/>......$s = new Searcher(<br/>.........new AndQuery(<br/>............new Query('type', 'Order'),<br/>............new Query('paid', 1),<br/>............new Query('area', $order-&gt;area),<br/>............new Query('category', $order-&gt;category)<br/>.........),<br/>.........array('order' =&gt; 'startTime DESC')<br/>......);<br/>......<br/>......if(count($s) == 0)<br/>.........return $this-&gt;basePrice;<br/>......<br/>......$currentPrice = $s[0]-&gt;listPrice;<br/>......<br/>......if(count($s) &gt; $this-&gt;capacity) {<br/>.........foreach(array_slice($s, 0 floor($this-&gt;sensitivity * $this-&gt;capacity) as $o)<br/>............if($o-&gt;listPrice != $currentPrice)<br/>...............return $currentPrice;<br/>.........return $this-&gt;increase($currentPrice);<br/>......}<br/>......<br/>......if(count($s) &lt;= $this-&gt;bottom &amp;&amp;<br/>.........time() - $s[0]-&gt;startTime &gt; $this-&gt;tolerance)<br/>.........return $this-&gt;decrease($currentPrice);<br/>.........<br/>......return $currentPrice;<br/>...}<br/>...<br/>...function increase($price) {<br/>......return $price + 10;<br/>...}<br/>...<br/>...function decrease($price) {<br/>......return $price - 10;<br/>...}<br/>}<br/><br/>class DingAds {<br/>...function ads() {<br/>......$v = new Visitor();<br/>......$s = new Searcher(<br/>.........new AndQuery(<br/>............new Query('type', 'Order'),<br/>............new Query('paid', 1),<br/>............new RangQuery('startTime', null, time() + 1),<br/>............new RangeQuery('endTime', time(), null),<br/>............new Query('categories', $v-&gt;category),<br/>............new InQuery('areas', $v-&gt;area-&gt;path())<br/>............)<br/>.........)<br/>......);<br/>......<br/>......$dingAds = array();<br/>......$negAds = array();<br/>......<br/>......foreach($s as $o)<br/>.........if($o-&gt;price &gt;= 0)<br/>............$dingAds[] = $o-&gt;id;<br/>.........else<br/>............$negAds[] = $o-&gt;id;<br/>............<br/>......return array_diff($dingAds, $negAds);<br/>...}<br/>}<br/>/**<br/>* 1. AccuralBasedAccouting DONE<br/>* 2. Ding (Category, Filter, City, Province, China) DONE<br/>* 3. Place order, pay, refund, partialCancel DONE<br/>* 4. Pricing (Ding bidding based pricing, Refresh bidding, list bidding) DONE<br/>* 5. User Account, real money, fake money DONE<br/>* 6. Ding display DONE<br/>* 7) Refresh 5 RMB<br/>* 8) listing fee 10 RMB<br/>* 9) Sales tools<br/>* 10) Automatic invoice<br/>* 11) Port service<br/>*<br/>* o-----o-----o-----o-----ø--<br/>* ^ time<br/>* o-----o-----o-----o-----o----<br/>*<br/>*.................................o-----o-----<br/>*<br/>* |------|------|------|------|------|------|------|<br/>* ^ startTime ^ endTime<br/>*<br/>* Order<br/>* =====<br/>* orderId<br/>* starTime<br/>* endTime<br/>* listPrice<br/>* price<br/>* paid<br/>* area<br/>* category<br/>* adId<br/>*<br/>* 1. Order never change after paid.<br/>* 2. Range [startTime, endTime)<br/>* 3. price, money refer to real money,<br/>* 4. listPrice, $credit refer to fake money.<br/>*/",
