function getBlog() {
    return blog;
}

function getCategories() {
    var categories = []; 
    for(var key in blog) {
        var cats = blog[key]['分类目录'].split('|');
        cats.forEach(function(i){
            categories[i] ++;
        });
    }

    var c = [];
    console.log(categories);
    for(var value in categories) {
        c.push({
            'Title': value
        });
    }
    return c;
}

module.exports.getBlog = getBlog;
module.exports.getCategories = getCategories;

